/* BOOLEAN.asm * Returns true iff the argument is a boolean */ BOOLEAN:  PUSH(FP);  MOV(FP, SP);    CMP(FPARG(1), IMM(1));  JUMP_LT(HAS_NO_ARGS_BOOLEAN);  JUMP_GT(TOO_MANY_ARGS_BOOLEAN);  MOV(R0,FPARG(2));  PUSH(R0);  CALL(IS_SOB_BOOL);  DROP(1);  CMP(R0,IMM(0));  JUMP_EQ(MAKE_FALSE_BOOLEAN);  MOV(R0,TRUE_CONSTANT);  JUMP(END_BOOLEAN);    MAKE_FALSE_BOOLEAN:  MOV(R0,FALSE_CONSTANT);    END_BOOLEAN:  POP(FP);  RETURN;    HAS_NO_ARGS_BOOLEAN:  CALL(ERROR_NOT_ENOUGH_ARGS);    TOO_MANY_ARGS_BOOLEAN:  CALL(ERROR_TOO_MANY_ARGS);  