/* cdr.asm * Returns the second argument of the given pair */ CDR:  PUSH(FP);  MOV(FP, SP);    CMP(FPARG(1), IMM(1));  JUMP_LT(HAS_NO_ARGS_CDR);  JUMP_GT(TOO_MANY_ARGS_CDR);  MOV(R0,FPARG(2));  CMP(IND(R0),IMM(T_PAIR));  JUMP_NE(NOT_VALID_ARG_CDR);  MOV(R0, INDD(R0,2));  POP(FP);  RETURN;    HAS_NO_ARGS_CDR:  CALL(ERROR_NOT_ENOUGH_ARGS);    TOO_MANY_ARGS_CDR:  CALL(ERROR_TOO_MANY_ARGS);    NOT_VALID_ARG_CDR:  CALL(ERROR_NOT_VALID_ARG);