/* CHAR.asm * Returns true iff the argument is a CHAR */ CHAR:  PUSH(FP);  MOV(FP, SP);    CMP(FPARG(1), IMM(1));  JUMP_LT(HAS_NO_ARGS_CHAR);  JUMP_GT(TOO_MANY_ARGS_CHAR);  MOV(R0,FPARG(2));  PUSH(R0);  CALL(IS_SOB_CHAR);  DROP(1);  CMP(R0,IMM(1));  JUMP_EQ(MAKE_TRUE_CHAR);    MOV(R0,FALSE_CONSTANT);  POP(FP);  RETURN;    MAKE_TRUE_CHAR:  MOV(R0,TRUE_CONSTANT);  POP(FP);  RETURN;      HAS_NO_ARGS_CHAR:  CALL(ERROR_NOT_ENOUGH_ARGS);    TOO_MANY_ARGS_CHAR:  CALL(ERROR_TOO_MANY_ARGS);