/* CHAR_TO_INT.asm * Takes a char and Returns in R0 the  Unicode scalar value of this char */ CHAR_TO_INT:  PUSH(FP);  MOV(FP, SP);    CMP(FPARG(1), IMM(1));  JUMP_LT(HAS_NO_ARGS_CHAR_TO_INT);  JUMP_GT(TOO_MANY_ARGS_CHAR_TO_INT);  CMP(IND(FPARG(2)),T_CHAR);  JUMP_NE(NOT_VALID_ARG_CHAR_TO_INT);    PUSH(INDD(FPARG(2),1));  CALL(MAKE_SOB_INTEGER);  DROP(1);    POP(FP);  RETURN;    NOT_VALID_ARG_CHAR_TO_INT:  CALL(ERROR_NOT_VALID_ARG);    HAS_NO_ARGS_CHAR_TO_INT:  CALL(ERROR_NOT_ENOUGH_ARGS);    TOO_MANY_ARGS_CHAR_TO_INT:  CALL(ERROR_TOO_MANY_ARGS);