/* CONS.asm * Takes 2 arguments and Create a Pair of them and return it at R0 */ CONS:  PUSH(FP);  MOV(FP, SP);  PUSH(R1);    CMP(FPARG(1), IMM(2));  JUMP_LT(HAS_NO_ARGS_CONS);  JUMP_GT(TOO_MANY_ARGS_CONS);  MOV(R0,FPARG(2));  MOV(R1,FPARG(3));  PUSH(R1);  PUSH(R0);  CALL(MAKE_SOB_PAIR);  DROP(2);    POP(R1);  POP(FP);  RETURN;    HAS_NO_ARGS_CONS:  CALL(ERROR_NOT_ENOUGH_ARGS);    TOO_MANY_ARGS_CONS:  CALL(ERROR_TOO_MANY_ARGS);