/* INTEGER.asm * Returns true iff the argument is an INTEGER */ INTEGER:  PUSH(FP);  MOV(FP, SP);    CMP(FPARG(1), IMM(1));  JUMP_LT(HAS_NO_ARGS_INTEGER);  JUMP_GT(TOO_MANY_ARGS_INTEGER);  MOV(R0,FPARG(2));  PUSH(R0);  CALL(IS_SOB_INTEGER);  DROP(1);  CMP(R0,IMM(1));  JUMP_EQ(MAKE_TRUE_INTEGER);    MOV(R0,FPARG(2));  PUSH(R0);  CALL(IS_SOB_FRACTION);  DROP(1);  CMP(R0,IMM(0));  JUMP_EQ(MAKE_FALSE_INTEGER);  MOV(R0,INDD(FPARG(2),1));  REM(R0,INDD(FPARG(2),2));  CMP(R0,IMM(0));  JUMP_EQ(MAKE_TRUE_INTEGER);    MAKE_FALSE_INTEGER:  MOV(R0,FALSE_CONSTANT);  POP(FP);  RETURN;    MAKE_TRUE_INTEGER:  MOV(R0,TRUE_CONSTANT);  POP(FP);  RETURN;      HAS_NO_ARGS_INTEGER:  CALL(ERROR_NOT_ENOUGH_ARGS);    TOO_MANY_ARGS_INTEGER:  CALL(ERROR_TOO_MANY_ARGS);