/* less_than.asm * Returns true iff all arguments are less than each other: R0 <- TRUE iff ARG[0] < ARG[1] <... */ LESS_THEN:  PUSH(FP);  MOV(FP, SP);  PUSH(R1);  PUSH(R3);  PUSH(R2);  PUSH(R4);    MOV(R4,FPARG(1));  CMP(R4, IMM(0));  JUMP_EQ(HAS_NO_ARGS_LT);  CALL(MAKE_ARGS_FRACTIONS);  DECR(R4);  MOV(R3,IMM(2));    L_START_LT:  CMP(R4, IMM(0));  JUMP_EQ(CREATE_TRUE_LT);  MOV(R2,INDD(FPARG(R3),1));  MOV(R1,INDD(FPARG(R3),2));  INCR(R3);  MUL(R2,INDD(FPARG(R3),2));  MUL(R1,INDD(FPARG(R3),1));  CMP(R2, R1);  JUMP_GE(CREATE_FALSE_LT);  DECR(R4);  JUMP(L_START_LT);    HAS_NO_ARGS_LT:  CALL(ERROR_NOT_ENOUGH_ARGS);    CREATE_TRUE_LT:  MOV(R0,TRUE_CONSTANT);  POP(R4);  POP(R2);  POP(R3);  POP(R1);  POP(FP);  RETURN;    CREATE_FALSE_LT:  MOV(R0,FALSE_CONSTANT);  POP(R4);  POP(R2);  POP(R3);  POP(R1);  POP(FP);  RETURN;