/* make_args_fractions.asm * Makes all arguments that are not fractions to be fractions */ MAKE_ARGS_FRACTIONS:  PUSH(R1);  MOV(R1, FPARG(1));  CMP(R1, IMM(0));  JUMP_EQ(L_FINISH_MAKE_ARGS_FRACTIONS);  INCR(R1);    L_START_MAKE_ARGS_FRACTIONS:  CMP(R1, IMM(1));  JUMP_EQ(L_FINISH_MAKE_ARGS_FRACTIONS);  MOV(R0,FPARG(R1));  PUSH(R0);  CALL(IS_SOB_FRACTION);  DROP(1);  CMP(R0, IMM(0));  JUMP_EQ(L_MAKE_FRACTION);  DECR(R1);  JUMP(L_START_MAKE_ARGS_FRACTIONS);    L_MAKE_FRACTION:  PUSH(IMM(1));  PUSH(INDD(FPARG(R1),1));  CALL(MAKE_SOB_FRACTION);  DROP(2);  MOV(FPARG(R1),R0);  DECR(R1);  JUMP(L_START_MAKE_ARGS_FRACTIONS);    L_FINISH_MAKE_ARGS_FRACTIONS:  POP(R1);  RETURN;