/* multiply.asm * Computes the multiplicity of variadic number of integers: R0 <- ARG[0] * ARG[1] *... */ MULTIPLICITY:  PUSH(FP);  MOV(FP, SP);  PUSH(R1);  PUSH(R2);  PUSH(R3);  PUSH(R4);    MOV(R4,FPARG(1));  CMP(R4, IMM(0));  JUMP_EQ(L_ZERO_ARGS_MUL);    CALL(MAKE_ARGS_FRACTIONS);  MOV(R0, INDD(FPARG(2),1));  MOV(R2, INDD(FPARG(2),2));  MOV(R1, IMM(3));  DECR(R4);    L_START_MUL:  CMP(R4, IMM(0));  JUMP_EQ(L_FINISH_MUL);  MUL(R0,INDD(FPARG(R1),1));  MUL(R2,INDD(FPARG(R1),2));  INCR(R1);  DECR(R4);  JUMP(L_START_MUL);    L_FINISH_MUL:  MOV(R3,R0);  REM(R3,R2);  CMP(R3, IMM(0));  JUMP_EQ(MAKE_INTEGER_MUL);    PUSH(R2);  PUSH(R0);  CALL(MAKE_SOB_FRACTION);  DROP(2);  JUMP(L_END_MUL);    MAKE_INTEGER_MUL:  DIV(R0,R2);  PUSH(R0);  CALL(MAKE_SOB_INTEGER);  DROP(1);    L_END_MUL:  POP(R4);  POP(R3);  POP(R2);  POP(R1);  POP(FP);  RETURN;    L_ZERO_ARGS_MUL:  PUSH(IMM(1));  CALL(MAKE_SOB_INTEGER);  DROP(1);  POP(R4);  POP(R3);  POP(R2);  POP(R1);  POP(FP);  RETURN;