/* NULL.asm * Returns true iff the argument is a NULL */ IS_NULL:  PUSH(FP);  MOV(FP, SP);    CMP(FPARG(1), IMM(1));  JUMP_LT(HAS_NO_ARGS_NULL);  JUMP_GT(TOO_MANY_ARGS_NULL);  MOV(R0,FPARG(2));  CMP(IND(R0), T_NIL);  JUMP_EQ(MAKE_TRUE_NULL);    MOV(R0,FALSE_CONSTANT);  POP(FP);  RETURN;    MAKE_TRUE_NULL:  MOV(R0,TRUE_CONSTANT);  POP(FP);  RETURN;      HAS_NO_ARGS_NULL:  CALL(ERROR_NOT_ENOUGH_ARGS);    TOO_MANY_ARGS_NULL:  CALL(ERROR_TOO_MANY_ARGS);