/* NUMBER.asm * Returns true iff the argument is a NUMBER */ NUMBER:  PUSH(FP);  MOV(FP, SP);    CMP(FPARG(1), IMM(1));  JUMP_LT(HAS_NO_ARGS_NUMBER);  JUMP_GT(TOO_MANY_ARGS_NUMBER);  MOV(R0,FPARG(2));  CMP(IND(R0), T_INTEGER);  JUMP_EQ(MAKE_TRUE_NUMBER);  CMP(IND(R0), T_FRACTION);  JUMP_EQ(MAKE_TRUE_NUMBER);    MOV(R0,FALSE_CONSTANT);  POP(FP);  RETURN;    MAKE_TRUE_NUMBER:  MOV(R0,TRUE_CONSTANT);  POP(FP);  RETURN;      HAS_NO_ARGS_NUMBER:  CALL(ERROR_NOT_ENOUGH_ARGS);    TOO_MANY_ARGS_NUMBER:  CALL(ERROR_TOO_MANY_ARGS);