/* PROCEDURE.asm * Returns true iff the argument is a PROCEDURE */ PROCEDURE:  PUSH(FP);  MOV(FP, SP);    CMP(FPARG(1), IMM(1));  JUMP_LT(HAS_NO_ARGS_PROCEDURE);  JUMP_GT(TOO_MANY_ARGS_PROCEDURE);  MOV(R0,FPARG(2));  CMP(IND(R0), T_CLOSURE);  JUMP_EQ(MAKE_TRUE_PROCEDURE);    MOV(R0,FALSE_CONSTANT);  POP(FP);  RETURN;    MAKE_TRUE_PROCEDURE:  MOV(R0,TRUE_CONSTANT);  POP(FP);  RETURN;      HAS_NO_ARGS_PROCEDURE:  CALL(ERROR_NOT_ENOUGH_ARGS);    TOO_MANY_ARGS_PROCEDURE:  CALL(ERROR_TOO_MANY_ARGS);