/* VECTOR_LENGTH.asm * Returns in R0 the length of a given vector */ VECTOR_LENGTH:  PUSH(FP);  MOV(FP, SP);    CMP(FPARG(1), IMM(1));  JUMP_LT(HAS_NO_ARGS_VECTOR_LENGTH);  JUMP_GT(TOO_MANY_ARGS_VECTOR_LENGTH);  CMP(IND(FPARG(2)),T_VECTOR);  JUMP_NE(NOT_VALID_ARG_VECTOR_LENGTH);    PUSH(INDD(FPARG(2),1));  CALL(MAKE_SOB_INTEGER);  DROP(1);  POP(FP);  RETURN;    NOT_VALID_ARG_VECTOR_LENGTH:  CALL(ERROR_NOT_VALID_ARG);    HAS_NO_ARGS_VECTOR_LENGTH:  CALL(ERROR_NOT_ENOUGH_ARGS);    TOO_MANY_ARGS_VECTOR_LENGTH:  CALL(ERROR_TOO_MANY_ARGS);