/* VECTOR.asm * Returns true iff the argument is a VECTOR */ VECTOR:  PUSH(FP);  MOV(FP, SP);    CMP(FPARG(1), IMM(1));  JUMP_LT(HAS_NO_ARGS_VECTOR);  JUMP_GT(TOO_MANY_ARGS_VECTOR);  MOV(R0,FPARG(2));  CMP(IND(R0), T_VECTOR);  JUMP_EQ(MAKE_TRUE_VECTOR);    MOV(R0,FALSE_CONSTANT);  POP(FP);  RETURN;    MAKE_TRUE_VECTOR:  MOV(R0,TRUE_CONSTANT);  POP(FP);  RETURN;      HAS_NO_ARGS_VECTOR:  CALL(ERROR_NOT_ENOUGH_ARGS);    TOO_MANY_ARGS_VECTOR:  CALL(ERROR_TOO_MANY_ARGS);